import React from "react";
import "./App.css";

class App extends React.Component {
  state = {
    word: "well",
  };

  getWord = () => {

    this.setState({ word: "ok"})
  }
  render() {
    const { word } = this.state
    return (
      <div className="App">
        <button onClick={this.getWord}></button>
        <p>{word}</p>
      </div>
    );
  }
}

export default App;
