import React from "react";
import fs from 'fs';
import "./App.css";

class App extends React.Component {
  state = {
    word: "well",
  };


  getWord = () => {
    const txtToJson = require("txt-file-to-json");
    const dataInJSON = txtToJSON({ filePath: "./russian_nouns_5.txt" });
    // fetch("wordle-ru/src/russian_nouns_5.txt") 
    //   .then((res) => {
    //   return res.json();
    // })
    //   .then ((data) => {
    //     console.log(data)
    //   })
    this.setState({ word: {dataInJSON} });
  };
  render() {
    const { word } = this.state;
    return (
      <div className="App">
        <button onClick={this.getWord}></button>
        <p>{word}</p>
      </div>
    );
  }
}

export default App;
